INSERT INTO `ficha_tecnicas` VALUES (1,'<h2>Descri&ccedil;&atilde;o</h2>\r\n\r\n<p>Kit M&oacute;veis Para Manicure 4 Pe&ccedil;as<br />\r\n<br />\r\nOP&Ccedil;&Otilde;ES DE CORES:<br />\r\n<br />\r\n+PRETO<br />\r\n+BRANCO<br />\r\n+VERMELHO<br />\r\n+ROSA PINK<br />\r\n+LILAS<br />\r\n<br />\r\nTEMOS TODAS EM ESTOQUE PARA ENVIO IMEDIATO<br />\r\n<br />\r\nAP&Oacute;S A COMPRA, FA&Ccedil;A UMA NOVA PERGUNTA OU ENVIE UM E-MAIL SOLICITANDO A COR DESEJADA.<br />\r\n<br />\r\nKIT MANICURE COMPOSTO POR:<br />\r\n<br />\r\n1 CIRANDINHA&nbsp;<br />\r\n<br />\r\n1 CADEIRA CLIENTE<br />\r\n<br />\r\n1 CARRINHO AUXILIAR PEQUENO COM 2 BANDEJAS<br />\r\n<br />\r\n1 SUPORTE TRIP&Eacute; PEDICURO<br />\r\n<br />\r\nCOMPOSTO DE FERRO NO TUBO 3/4 DE PAREDE 0,90<br />\r\n<br />\r\nPINTURA ELESTROST&Aacute;TICA<br />\r\n<br />\r\nESPUMA DE DENSIDADE D-23 E CORINO.</p>','LJ MÃ³veis','K4P',5,'2019-01-11 19:23:37','2019-01-11 19:23:37'),(2,'<h2>Descri&ccedil;&atilde;o</h2>\r\n\r\n<p>KIT MANICURE FORMADO COM AS SEGUINTES PE&Ccedil;AS&nbsp;<br />\r\n<br />\r\n1 MALETA FRASQUEIRA&nbsp;<br />\r\n1 AMOLECEDOR DE CUT&Iacute;CULAS<br />\r\n8 ESMALTES IMPALA CORES DO ANUNCIO&nbsp;<br />\r\n1 BASE VITAMINADA IMPALA<br />\r\n1 &Oacute;LEO SECANTE IMPALA<br />\r\n1 REMOVEDOR DE ESMALTES 90ML&nbsp;<br />\r\n6 LIXAS POLIDORAS PARA UNHAS<br />\r\n60 MINI LIXAS ECO&nbsp;<br />\r\n2 SEPARADORES DE PEDICURE<br />\r\n25 PALITOS DE BAMBU&nbsp;<br />\r\n1 PACOTE DE ALGOD&Atilde;O BOLINHA&nbsp;<br />\r\n1 PORTA ACETONA 180ML<br />\r\n1 ALICATE MUNDIAL PROFISSIONAL INOX&nbsp;<br />\r\n1 EMPURRADOR INOX&nbsp;<br />\r\n1 PALITO INOX&nbsp;<br />\r\n1 CORTADO DE UNHAS MUNDIAL A&Ccedil;O CROMADO&nbsp;<br />\r\n<br />\r\nMAIS UM BRINDE - NECESSAIRE RUBYS&nbsp;<br />\r\n<br />\r\nTEMOS 4 CORES DE MALETA&nbsp;<br />\r\nPRETA - PRETA POA -&nbsp;<br />\r\n<br />\r\nAGUARDAMOS SEU PEDIDO</p>','Variados','Bella FiFi',8,'2019-01-11 19:24:55','2019-01-11 19:24:55'),(3,'<h2>informa&ccedil;&otilde;es do produto</h2>\r\n\r\n